import React from 'react';
import { createMaterialTopTabNavigator } from '@react-navigation/material-top-tabs';
import { Image, TouchableOpacity } from 'react-native';
import { Ionicons } from '@expo/vector-icons'; // Import Ionicons for the chat icon
import ForYouScreen from './ForYouScreen';
import FollowingScreen from './FollowingsScreen';
import logo from '../assets/logo.png'; // Import your logo image

const Tab = createMaterialTopTabNavigator();

const HomeScreen = ({ navigation }) => {
  return (
    <Tab.Navigator
      screenOptions={{
        tabBarLabelStyle: { fontWeight: 'bold' },
        tabBarIndicatorStyle: { backgroundColor: '#007AFF' },
        tabBarStyle: { backgroundColor: '#fff' },
      }}
    >
      <Tab.Screen
        name="For You"
        component={ForYouScreen}
        options={{
          tabBarLabel: 'For You', // Tab label
          tabBarIcon: ({ color }) => (
            <Image source={logo} style={{ width: 30, height: 30, tintColor: color }} />
          ),
          headerTitle: () => (
            <Image source={logo} style={{ width: 100, height: 40, resizeMode: 'contain' }} />
          ),
        }}
      />
      <Tab.Screen
        name="Following"
        component={FollowingScreen}
        options={{
          tabBarLabel: 'Following', // Tab label
          headerRight: () => (
            <TouchableOpacity onPress={() => navigation.navigate('Chat')}>
              <Ionicons name="chatbubble-outline" size={24} color="black" />
            </TouchableOpacity>
          ),
        }}
      />
    </Tab.Navigator>
  );
};

export default HomeScreen;
