import React, { useEffect, useContext, useState, useCallback } from "react";
import { StyleSheet, Text, View, ScrollView, Image, RefreshControl } from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import jwt_decode from "jwt-decode";
import { UserType } from "../UserContext";
import axios from "axios";
import { AntDesign } from "@expo/vector-icons";
import { FontAwesome } from "@expo/vector-icons";
import { Ionicons } from "@expo/vector-icons";
import { useFocusEffect } from "@react-navigation/native";
import logo from '../assets/logo.png';

const HomeScreen = ({ route }) => {
  const { userId, setUserId } = useContext(UserType);
  const [posts, setPosts] = useState([]);
  const [refreshing, setRefreshing] = useState(false);
  const [userIdSet, setUserIdSet] = useState(false); // Track whether userId has been set

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = await AsyncStorage.getItem("authToken");
        if (!token) {
          console.log("Token not found");
          return;
        }
        const decodedToken = jwt_decode(token);
        if (!decodedToken || !decodedToken.userId) {
          console.log("Decoded token or user ID not found");
          return;
        }
        const userId = decodedToken.userId;
        console.log("Decoded User ID:", userId);
        setUserId(userId);
        setUserIdSet(true); // Set userIdSet to true after userId is set
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };
    
    fetchUserData();
  }, [route]);
   // Include setUserId as dependency

  useEffect(() => {
    if (userIdSet) {
      fetchPosts(); // Call fetchPosts only after userId has been set
    }
  }, [userIdSet]);

  const onRefresh = useCallback(() => {
    setRefreshing(true);
    fetchPosts();
    setRefreshing(false);
  }, []);

  const fetchPosts = async () => {
    try {
      const token = await AsyncStorage.getItem("authToken");
      const headers = {
        Authorization: `Bearer ${token}`,
        "Content-Type": "application/json",
      };

      // Check if userId is not empty or undefined
      if (!userId) {
        console.log("User ID not found");
        return;
      }

      // Fetch the user's followings
      const responseFollowings = await axios.get(`https://levelartbackend-production.up.railway.app/followers/${userId}/following`, { headers });
      const followings = responseFollowings.data;

      // Fetch posts from the followings
      const postsPromises = followings.map(async (followingId) => {
        const response = await axios.get(`https://levelartbackend-production.up.railway.app/posts/user/${followingId}`, { headers });
        return response.data;
      });

      // Wait for all post requests to complete
      const postsResponses = await Promise.all(postsPromises);

      // Combine all posts into a single array
      const allPosts = postsResponses.reduce((accumulator, currentPosts) => accumulator.concat(currentPosts), []);

      // Set the posts state
      setPosts(allPosts);
      console.log("Posts from followings:", allPosts);
    } catch (error) {
      console.log("Error fetching posts", error);
    }
  };
  
  const handleLike = async (postId) => {
    try {
      // Code for handling like
    } catch (error) {
      console.log("Error liking the post", error);
    }
  };

  const handleDislike = async (postId) => {
    try {
      // Code for handling dislike
    } catch (error) {
      console.error("Error unliking post:", error);
    }
  };

  return (
    <ScrollView
      style={{ marginTop: 50, flex: 1, backgroundColor: "white" }}
      refreshControl={<RefreshControl refreshing={refreshing} onRefresh={onRefresh} />}
    >
      <View style={{ alignItems: "center", marginTop: 20 }}>
        <Image
          style={{ width: 60, height: 40, resizeMode: "contain" }}
          source={logo}
        />
      </View>

      <View style={{ marginTop: 20 }}>
        {posts?.map((post) => (
          <View
            key={post._id}
            style={{
              padding: 15,
              borderColor: "#D0D0D0",
              borderTopWidth: 1,
              flexDirection: "row",
              gap: 10,
              marginVertical: 10,
            }}
          >
            <Image
              source={{ uri: post.user.profilePicture }}
              style={{ width: 50, height: 50, borderRadius: 25 }}
            />
            <View style={{ flex: 1 }}>
              <Text style={{ fontWeight: "bold" }}>{post.user.username}</Text>
              <Text>{post.text}</Text>
            </View>
            <View style={{ flexDirection: "row", alignItems: "center" }}>
              <AntDesign
                name="hearto"
                size={24}
                color="black"
                onPress={() => handleLike(post._id)}
              />
              <Text style={{ marginHorizontal: 5 }}>{post.likes.length}</Text>
              <FontAwesome
                name="comment-o"
                size={24}
                color="black"
                onPress={() => {
                  // Navigate to the post details screen or open a modal for comments
                }}
              />
              <Text style={{ marginHorizontal: 5 }}>{post.comments.length}</Text>
              <Ionicons
                name="share-social-outline"
                size={24}
                color="black"
                onPress={() => {
                  // Implement share functionality
                }}
              />
            </View>
          </View>
        ))}
      </View>
    </ScrollView>
  );
};

export default HomeScreen;

const styles = StyleSheet.create({});
