import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View, Image, SafeAreaView, TextInput, Button, ActivityIndicator } from "react-native";
import axios from "axios";
import AsyncStorage from "@react-native-async-storage/async-storage";

const ThreadsScreen = () => {
  const [postedBy, setPostedBy] = useState("");
  const [content, setContent] = useState("");
  const [isLoading, setIsLoading] = useState(true);

  console.log("postedBy:",postedBy)

  useEffect(() => {

    const fetchUserId = async () => {
      try {

        const storedUserId = await AsyncStorage.getItem("userId");

        const response = await axios.get("https://levelartbackend-production.up.railway.app/users")

        const userId = response.

        console.log("userId:", userId);
        if (storedUserId) {
          setPostedBy(storedUserId);
        } else {
          console.log("User ID not found in AsyncStorage");
        }
      } catch (error) {
        console.error("Error fetching userId from AsyncStorage:", error);
      } finally {
        setIsLoading(false);
      }
    };
    
    
  
    fetchUserId();
  }, []);
  
  
  
  const handlePostSubmit = async () => {
    if (!content) {
      console.log("Text field is required");
      return;
    }
  
    try {
      // Retrieve the user ID from AsyncStorage
      const userId = await AsyncStorage.getItem("userId");
      if (!userId) {
        console.log("User ID not found in AsyncStorage");
        return;
      }
  
      // Create the postData object with the user ID and content
      const postData = {
        postedBy: userId, // Pass the userId as postedBy
        text: content, // Pass the content as text
      };
  
      // Retrieve the authentication token from AsyncStorage
      const authToken = await AsyncStorage.getItem("authToken");
      if (!authToken) {
        console.log("No authentication token found in AsyncStorage");
        return;
      }
  
      // Include the token in the request header
      const response = await axios.post("https://levelartbackend-production.up.railway.app/posts/create-post", postData, {
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
      });
  
      // Clear the content field after successful post creation
      setContent("");
      console.log("Post created successfully:", response.data);
    } catch (error) {
      console.log("Error creating post:", error);
    }
  };
  
  
  if (isLoading) {
    return (
      <SafeAreaView style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
        <ActivityIndicator size="large" color="black" />
      </SafeAreaView>
    );
  }

  return (
    <SafeAreaView style={{ padding: 10 }}>
      <View
        style={{
          flexDirection: "row",
          alignItems: "center",
          gap: 10,
          padding: 10,
        }}
      >
        <Image
          style={{
            width: 40,
            height: 40,
            borderRadius: 20,
            resizeMode: "contain",
          }}
          source={{
            uri: "https://cdn-icons-png.flaticon.com/128/149/149071.png",
          }}
        />
      </View>

      <View style={{ flexDirection: "row", marginLeft: 10 }}>
        <TextInput
          value={content}
          onChangeText={(text) => setContent(text)}
          placeholderTextColor={"black"}
          placeholder="Type your message..."
          multiline
        />
      </View>

      <View style={{ marginTop: 20 }} />

      <Button onPress={handlePostSubmit} title="Share Post" />
    </SafeAreaView>
  );
};

export default ThreadsScreen;

const styles = StyleSheet.create({});
