import React, { useState } from "react";
import { View, TextInput, Button, ActivityIndicator, TouchableOpacity, Image } from "react-native";
import * as ImagePicker from 'expo-image-picker'; 
import axios from "axios";
import AsyncStorage from "@react-native-async-storage/async-storage";
import jwt_decode from "jwt-decode";

const ThreadsScreen = ({ navigation }) => {
  const [content, setContent] = useState('');
  const [image, setImage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleImagePicker = async () => {
    // Permission check
    const permissionResult = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (permissionResult.granted === false) {
      alert('Permission to access camera roll is required!');
      return;
    }
  
    // Launch image picker
    const pickerResult = await ImagePicker.launchImageLibraryAsync();
    if (!pickerResult.cancelled && pickerResult.assets.length > 0) {
      setImage(pickerResult.assets[0].uri);
    }
  };
  

  const handlePostSubmit = async () => {
    setIsLoading(true);
  
    try {
      const authToken = await AsyncStorage.getItem('authToken');
      const decodedToken = jwt_decode(authToken);
      const userId = decodedToken.sub; 
  
      const formData = new FormData();
      formData.append('text', content);
      formData.append('postedBy', userId); 
  
      if (image) {
        const localUri = image;
        const filename = localUri.split('/').pop();
        const match = /\.(\w+)$/.exec(filename);
        const type = match ? `image/${match[1]}` : 'image';
        formData.append('img', { uri: localUri, name: filename, type }); 
      }
  
      const response = await axios.post('https://levelartbackend-production.up.railway.app/posts/create-post', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${authToken}`,
        },
      });
  
      console.log('Post created successfully:', response.data);
      
      // Pass the newly created post data back to HomeScreen
      navigation.navigate('Home', { newPost: response.data });
      
    } catch (error) {
      console.error('Error creating post:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <View style={{ flex: 1, padding: 20 }}>
      <TextInput
        value={content}
        onChangeText={setContent}
        placeholder="What's on your mind?"
        multiline
        style={{ marginBottom: 20, borderBottomWidth: 1, borderColor: '#ccc' }}
      />
      <TouchableOpacity onPress={handleImagePicker}>
        <View style={{ marginBottom: 20 }}>
          <Text>Add Image</Text>
        </View>
      </TouchableOpacity>
      {image && <Image source={{ uri: image }} style={{ width: 200, height: 200, marginBottom: 20 }} />}
      <Button title="Share Post" onPress={handlePostSubmit} disabled={isLoading} />
      {isLoading && <ActivityIndicator />}
    </View>
  );
};

export default ThreadsScreen;
