import React, { useEffect, useContext, useState, useCallback } from "react";
import { StyleSheet, Text, View, ScrollView, Image, RefreshControl, TouchableOpacity } from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import jwt_decode from "jwt-decode";
import { UserType } from "../UserContext";
import axios from "axios";
import { AntDesign } from "@expo/vector-icons";
import { FontAwesome } from "@expo/vector-icons";
import { Ionicons } from "@expo/vector-icons";
import { useFocusEffect } from "@react-navigation/native";
import logo from '../assets/logo.png';

const HomeScreen = ({ navigation }) => {
  const { userId, setUserId } = useContext(UserType);
  const [posts, setPosts] = useState([]);
  const [refreshing, setRefreshing] = useState(false);

  useEffect(() => {
    const fetchUserData = async () => {
      const token = await AsyncStorage.getItem("authToken");
      const decodedToken = jwt_decode(token);
      const userId = decodedToken.userId;
      setUserId(userId);
    };
    
    fetchUserData();
  }, []);

  useEffect(() => {
    fetchPosts();
  }, []);

  useFocusEffect(
    useCallback(() => {
      fetchPosts();
    }, [])
  );

  const onRefresh = useCallback(() => {
    setRefreshing(true);
    fetchPosts();
    setRefreshing(false);
  }, []);

  const fetchPosts = async () => {
    try {
      const token = await AsyncStorage.getItem("authToken");
      const headers = {
        Authorization: `Bearer ${token}`,
        "Content-Type": "application/json",
      };
  
      // Check if userId is not empty or undefined
      if (!userId) {
        console.log("User ID not found");
        return;
      }
  
      // Fetch the user's followings
      const responseFollowings = await axios.get(`https://levelartbackend-production.up.railway.app/followers/${userId}/following`, { headers });
      const followings = responseFollowings.data;
  
      // Fetch posts from the followings
      const postsPromises = followings.map(async (followingId) => {
        const response = await axios.get(`https://levelartbackend-production.up.railway.app/posts/user/${followingId}`, { headers });
        return response.data;
      });
  
      // Wait for all post requests to complete
      const postsResponses = await Promise.all(postsPromises);
  
      // Combine all posts into a single array
      const allPosts = postsResponses.reduce((accumulator, currentPosts) => accumulator.concat(currentPosts), []);
  
      // Set the posts state
      setPosts(allPosts);
      console.log("Posts from followings:", allPosts);
    } catch (error) {
      console.log("Error fetching posts", error);
    }
  };
  

  const handleLike = async (postId) => {
    try {
      // Implement like functionality
    } catch (error) {
      console.log("Error liking the post", error);
    }
  };

  const handleComment = (postId) => {
    // Navigate to the post details screen or open a modal for comments
    navigation.navigate("PostDetails", { postId });
  };

  const handleShare = (postId) => {
    // Implement share functionality
  };

  return (
    <ScrollView
      style={{ marginTop: 50, flex: 1, backgroundColor: "white" }}
      refreshControl={<RefreshControl refreshing={refreshing} onRefresh={onRefresh} />}
    >
      <View style={{ alignItems: "center", marginTop: 20 }}>
        <Image
          style={{ width: 60, height: 40, resizeMode: "contain" }}
          source={logo}
        />
      </View>

      <View style={{ marginTop: 20 }}>
        {posts.map((post) => (
          <View
            key={post._id}
            style={{
              padding: 15,
              borderColor: "#D0D0D0",
              borderTopWidth: 1,
              flexDirection: "row",
              gap: 10,
              marginVertical: 10,
            }}
          >
            <Image
              source={{ uri: post.user.profilePicture }}
              style={{ width: 50, height: 50, borderRadius: 25 }}
            />
            <View style={{ flex: 1 }}>
              <Text style={{ fontWeight: "bold" }}>{post.user.username}</Text>
              <Text>{post.text}</Text>
            </View>
            <View style={{ flexDirection: "row", alignItems: "center" }}>
              <TouchableOpacity onPress={() => handleLike(post._id)}>
                <AntDesign
                  name="hearto"
                  size={24}
                  color="black"
                />
              </TouchableOpacity>
              <Text style={{ marginHorizontal: 5 }}>{post.likes.length}</Text>
              <TouchableOpacity onPress={() => handleComment(post._id)}>
                <FontAwesome
                  name="comment-o"
                  size={24}
                  color="black"
                />
              </TouchableOpacity>
              <Text style={{ marginHorizontal: 5 }}>{post.comments.length}</Text>
              <TouchableOpacity onPress={() => handleShare(post._id)}>
                <Ionicons
                  name="share-social-outline"
                  size={24}
                  color="black"
                />
              </TouchableOpacity>
            </View>
          </View>
        ))}
      </View>
    </ScrollView>
  );
};

export default HomeScreen;

const styles = StyleSheet.create({});
