import React, { useEffect, useContext, useState, useCallback } from "react";
import { StyleSheet, Text, View, ScrollView, Image, RefreshControl, TouchableOpacity, Modal, TextInput } from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import jwt_decode from "jwt-decode";
import axios from "axios";
import { AntDesign, FontAwesome, Ionicons, Feather } from "@expo/vector-icons";
import logo from '../assets/logo.png';
import { UserType } from "../UserContext";
import MessageContainer from "../components/messageContainer"; // Import MessageContainer component

const HomeScreen = ({ route }) => {
  const { userId, setUserId } = useContext(UserType);
  const [posts, setPosts] = useState([]);
  const [refreshing, setRefreshing] = useState(false);
  const [userIdSet, setUserIdSet] = useState(false);
  const [showChat, setShowChat] = useState(false); // State to manage chat screen visibility
  const [savedPosts, setSavedPosts] = useState([]); // State to store saved posts

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = await AsyncStorage.getItem("authToken");
        if (!token) {
          console.log("Token not found");
          return;
        }
        const decodedToken = jwt_decode(token);
        if (!decodedToken || !decodedToken.sub) {
          console.log("Decoded token or user ID not found");
          return;
        }
        const userId = decodedToken.sub;
        setUserId(userId);
        setUserIdSet(true);
        fetchPosts(); // Fetch posts whenever userId changes
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };
    fetchUserData();
  }, [route]);

  useEffect(() => {
    if (userIdSet) {
      fetchPosts();
    }
  }, [userIdSet]);

  const onRefresh = useCallback(() => {
    setRefreshing(true);
    fetchPosts();
    setRefreshing(false);
  }, []);

  const fetchPosts = async () => {
    try {
      const token = await AsyncStorage.getItem("authToken");
      const decodedToken = jwt_decode(token);
      const headers = {
        Authorization: `Bearer ${token}`,
        "Content-Type": "application/json",
      };

      const responseFollowings = await axios.get(`https://levelart.up.railway.app/followers/${userId}/following`, { headers });
      const followings = responseFollowings.data;
      const postsPromises = followings.map(async (followingId) => {
        const response = await axios.get(`https://levelart.up.railway.app/user/${followingId}`, { headers });
        return response.data;
      });

      const responseUserPosts = await axios.get(`https://levelart.up.railway.app/posts/user/${userId}`, { headers });
      const userPosts = responseUserPosts.data;
      const postsResponses = await Promise.all(postsPromises);

      const allPosts = postsResponses.reduce((accumulator, currentPosts) => accumulator.concat(currentPosts), []);

      const combinedPosts = [...allPosts, ...userPosts];
      setPosts(combinedPosts);
    } catch (error) {
      console.log("Error fetching posts", error);
    }
  };

  const handleSavePost = async (postId) => {
    try {
      const isSaved = savedPosts.includes(postId);
      const updatedSavedPosts = isSaved
        ? savedPosts.filter((savedPostId) => savedPostId !== postId)
        : [...savedPosts, postId];
      setSavedPosts(updatedSavedPosts);
      await AsyncStorage.setItem("savedPosts", JSON.stringify(updatedSavedPosts));
    } catch (error) {
      console.error("Error saving post:", error);
    }
  };

  const renderPost = (post) => {
    return (
      <View key={`${post.postedBy}-${post.postId}`} style={styles.post}>
        <View style={styles.postHeader}>
          <Text style={styles.username}>{post.postedBy}</Text>
        </View>
        <View style={styles.postContent}>
          <Text>{post.text}</Text>
          {post.img && (
            <Image source={{ uri: post.img }} style={styles.postImage} />
          )}
          <View style={styles.actionButtons}>
            <TouchableOpacity onPress={() => handleSavePost(post._id)} style={styles.saveButton}>
              <Feather name={savedPosts.includes(post._id) ? "bookmark" : "bookmark-outline"} size={30} color={savedPosts.includes(post._id) ? "#000" : "#8e8e8e"} />
            </TouchableOpacity>
          </View>
        </View>
      </View>
    );
  };
  

  return (
    <ScrollView
      style={styles.container}
      refreshControl={<RefreshControl refreshing={refreshing} onRefresh={onRefresh} />}
    >
      <Image style={styles.logo} source={logo} />
      <TouchableOpacity style={styles.chatIconContainer} onPress={() => setShowChat(!showChat)}>
        <Ionicons name="chatbubble-outline" size={24} color="black" />
      </TouchableOpacity>
      <View style={styles.header}>
        <TouchableOpacity style={styles.sidebarButton} onPress={() => console.log("Following pressed")}>
          <Text style={styles.sidebarButtonText}>Following</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.sidebarButton} onPress={() => console.log("For You pressed")}>
          <Text style={styles.sidebarButtonText}>For You</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.postContainer}>
        {posts.map((post) => renderPost(post))}
      </View>
      {showChat && <MessageContainer />}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "white",
  },
  header: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    marginTop: 0,
    marginBottom: 0,
    borderBottomWidth: 1,
    borderBottomColor: "#ddd",
    paddingHorizontal: 20,
  },
  logo: {
    width: 80,
    height: 60,
    top: 10,
    right: 0,
    resizeMode: "contain",
  },
  sidebarButton: {
    paddingHorizontal: 50,
    paddingVertical: 10,
    borderRadius: 5,
  },
  sidebarButtonText: {
    color: "black",
  },
  chatIconContainer: {
    position: "absolute",
    top: 20,
    right: 10,
  },
  postContainer: {
    paddingHorizontal: 5,
  },
  post: {
    backgroundColor: "#fff",
    borderRadius: 10,
    marginBottom: 20,
    borderWidth: 1,
    borderColor: "#ddd",
  },
  postHeader: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    paddingHorizontal: 10,
    paddingVertical: 8,
  },
  profilePicture: {
    width: 40,
    height: 10,
    borderRadius: 20,
    marginRight: 10,
  },
  username: {
    fontWeight: "bold",
  },
  postContent: {
    minHeight: 100,
    paddingVertical: 10,
  },
  postImage: {
    width: "100%",
    height: 550,
  },
  actionButtons: {
    flexDirection: "row",
    paddingHorizontal: 5,
    paddingVertical: 10,
    borderTopWidth: 1,
    borderColor: "#ddd",
  },
  actionButton: {
    marginHorizontal: 5,
  },
  likes: {
    fontWeight: "bold",
    paddingHorizontal: 10,
    marginBottom: 5,
  },
  commentInputContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginTop: 10,
    paddingHorizontal: 10,
  },
  commentInput: {
    flex: 1,
    height: 40,
    borderWidth: 1,
    borderColor: "#ddd",
    borderRadius: 5,
    paddingHorizontal: 10,
  },
  postCommentButton: {
    marginLeft: 10,
    paddingVertical: 8,
    paddingHorizontal: 12,
    backgroundColor: "#007AFF",
    borderRadius: 5,
  },
  postCommentButtonText: {
    color: "#fff",
  },
});

export default HomeScreen;
