// Import useState
import React, { useEffect, useState, useContext } from "react";
import {
  StyleSheet,
  View,
  Text,
  Image,
  TouchableOpacity,
  SafeAreaView,
  ActivityIndicator, // Import ActivityIndicator
} from "react-native";
import { useNavigation } from "@react-navigation/native";
import { UserType } from "../UserContext";
import Icon from "react-native-vector-icons/Ionicons";
import { createMaterialTopTabNavigator } from "@react-navigation/material-top-tabs";
import axios from "axios";
import AsyncStorage from "@react-native-async-storage/async-storage";
import ImagesScreen from "./ImagesScreen";
import TweetsScreen from "./TweetsScreen";
import {
  MaterialCommunityIcons,
  AntDesign,
  MaterialIcons,
} from "@expo/vector-icons";
import * as ImagePicker from "expo-image-picker";

const Tab = createMaterialTopTabNavigator();

const ImagesTabScreen = ({ posts }) => <ImagesScreen posts={posts} />;
const TweetsTabScreen = ({ posts }) => <TweetsScreen posts={posts} />;
const TagTabScreen = ({ posts }) => <TweetsScreen posts={posts} />;

const ProfileScreen = () => {
  const navigation = useNavigation();
  const { userId } = useContext(UserType);
  const [userProfile, setUserProfile] = useState(null);
  const [followers, setFollowers] = useState(0);
  const [followings, setFollowings] = useState(0);
  const [posts, setPosts] = useState([]);
  const [bio, setBio] = useState("");
  const [image, setImage] = useState("");
  const [isImageSelected, setIsImageSelected] = useState(false);
  const [loggedInUserId, setLoggedInUserId] = useState(null);
  const [isFollowing, setIsFollowing] = useState(false); // State to track if the logged-in user is following the profile user
  const [loading, setLoading] = useState(true); // State to track loading state

  const handleImagePicker = async () => {
    // Function to handle image picking
  };

  const handleFollow = async () => {
    try {
      // Implement your logic for following/unfollowing here
      console.log("Handle follow logic here");
      // Example: Invert the isFollowing state to simulate toggling follow/unfollow
      setIsFollowing((prevState) => !prevState);
    } catch (error) {
      console.error("Error handling follow:", error);
    }
  };

  useEffect(() => {
    const fetchLoggedInUserId = async () => {
      try {
        const loggedInUserId = await AsyncStorage.getItem("userId");
        setLoggedInUserId(loggedInUserId);
      } catch (error) {
        console.error("Error fetching logged-in user's ID:", error);
      }
    };

    fetchLoggedInUserId();
  }, []);

  useEffect(() => {
    const fetchProfile = async (userId) => {
      try {
        const authToken = await AsyncStorage.getItem("authToken");
        const GetUser = await axios.get(
          `https://levelart.up.railway.app/users/${userId}`,
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
            },
          }
        );
        setUserProfile(GetUser.data);

        const profileResponse = await axios.get(
          `https://levelart.up.railway.app/followers/${userId}/followers`,
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
            },
          }
        );

        if (profileResponse) {
          const { user, followers, followings, bio } = profileResponse.data;
          setUserProfile(user);
          setFollowers(followers?.length || 0);
          setFollowings(followings?.length || 0);
          setBio(bio || "");

          // Check if the logged-in user is following the profile user
          if (loggedInUserId) {
            const isFollowingResponse = await axios.get(
              `https://levelart.up.railway.app/followers/${userId}/is-following/${loggedInUserId}`,
              {
                headers: {
                  Authorization: `Bearer ${authToken}`,
                },
              }
            );
            setIsFollowing(isFollowingResponse.data.isFollowing);
          }

          const postsResponse = await axios.get(
            `https://levelart.up.railway.app/posts/user/${userId}`,
            {
              headers: {
                Authorization: `Bearer ${authToken}`,
              },
            }
          );

          if (postsResponse) {
            setPosts(postsResponse.data);
          }
        }
        setLoading(false); // Set loading to false after fetching profile
      } catch (error) {
        console.error("Error fetching profile:", error);
        setLoading(false); // Set loading to false if an error occurs
      }
    };

    if (userId) {
      fetchProfile(userId);
    } else {
      console.log("userId is undefined");
    }
  }, [userId, loggedInUserId]);

  // Render loading indicator while loading
  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="blue" />
      </View>
    );
  }

  return (
    <>
      <SafeAreaView style={styles.container}>
        {/* Profile UI */}
        {/* Follow button should only be shown to other users */}
        {!loading && (
          <View style={styles.profileTop}>
            <View style={styles.userInfo}>
              <TouchableOpacity
                onPress={handleImagePicker}
                style={{ position: "relative" }}
              >
                {/* Profile Image */}
                {userProfile?.profilePic ? (
                  <Image
                    style={styles.profileImage}
                    source={{ uri: userProfile?.profilePic }}
                  />
                ) : (
                  <Image
                    style={styles.profileImage}
                    source={{
                      uri:
                        "https://cdn-icons-png.flaticon.com/128/149/149071.png",
                    }}
                  />
                )}

                {/* Edit Profile Picture Button */}
                <TouchableOpacity
                  onPress={handleImagePicker}
                  style={{ position: "absolute", bottom: -3, right: -3 }}
                >
                  <AntDesign name="pluscircle" size={24} color="blue" />
                </TouchableOpacity>
              </TouchableOpacity>

              {/* User Stats */}
              <View style={styles.userStats}>
                <View style={styles.userStatsItem}>
                  <Text style={styles.statsCount}>{posts.length}</Text>
                  <Text style={styles.statsText}> posts</Text>
                </View>
                <View style={styles.userStatsItem}>
                  <Text style={styles.statsCount}>{followers}</Text>
                  <Text style={styles.statsText}> followers</Text>
                </View>
                <View style={styles.userStatsItem}>
                  <Text style={styles.statsCount}>{followings} </Text>
                  <Text style={styles.statsText}>following</Text>
                </View>
              </View>
            </View>

            {/* Follow Button */}
            {loggedInUserId && loggedInUserId !== userProfile?._id && (
              <TouchableOpacity
                style={styles.followButton}
                onPress={handleFollow}
              >
                <Text style={styles.followButtonText}>
                  {isFollowing ? "Unfollow" : "Follow"}
                </Text>
              </TouchableOpacity>
            )}
          </View>
        )}

        {/* Profile Info */}
        {/* Render user bio and other details */}

        {/* Tab Navigator */}
        {/* Render tabs for Images, Tweets, and Tags */}
      </SafeAreaView>
    </>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "white",
  },
  loadingContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  profileTop: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    paddingHorizontal: 10,
    paddingVertical: 10,
  },
  followButton: {
    backgroundColor: "blue",
    paddingHorizontal: 20,
    paddingVertical: 10,
    borderRadius: 5,
  },
  followButtonText: {
    color: "white",
    fontWeight: "bold",
  },
  profileInfo: {
    flexDirection: "column",
    marginBottom: 20,
  },
  userStats: {
    marginLeft: 20,
    flexDirection: "row",
    flex: 1,
    justifyContent: "space-evenly",
    alignItems: "center",
  },
  userStatsItem: {
    alignItems: "center",
  },
  statsText: {
    fontSize: 14,
    fontWeight: "500",
  },
  statsCount: {
    fontWeight: "bold",
    fontSize: 22,
  },
});

export default ProfileScreen;
