// Sidebar.js
import React from "react";
import { StyleSheet, Text, View, Pressable } from "react-native";
import Icon from 'react-native-vector-icons/Ionicons';

const Sidebar = ({ isOpen, onClose }) => {
  return (
    <View style={[styles.sidebar, isOpen ? styles.openSidebar : styles.closedSidebar]}>
      <Pressable onPress={onClose}>
        <Icon name="close-outline" size={24} color="black" style={styles.closeIcon} />
      </Pressable>
      <Text style={styles.sidebarOption}>Accounts Center</Text>
      <Text style={styles.sidebarOption}>Notification</Text>
      <Text style={styles.sidebarOption}>Security & Account Access</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  sidebar: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: 250,
    height: '100%',
    backgroundColor: '#FFF',
    zIndex: 100,
    paddingTop: 50, // Adjust according to your design
    paddingLeft: 20, // Adjust according to your design
  },
  openSidebar: {
    transform: [{ translateX: 0 }],
  },
  closedSidebar: {
    transform: [{ translateX: -250 }],
  },
  sidebarOption: {
    fontSize: 18,
    marginBottom: 20,
  },
  closeIcon: {
    position: 'absolute',
    top: 20,
    right: 20,
  },
});

export default Sidebar;
