import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  Pressable,
  TextInput,
  KeyboardAvoidingView,
  Image,
  Alert,
} from "react-native";
import { MaterialIcons } from "@expo/vector-icons";
import { AntDesign } from "@expo/vector-icons";
import { Ionicons } from "@expo/vector-icons";
import { useNavigation } from "@react-navigation/native";
import logo from "../assets/logo.png";
import axios from "axios"; // Import axios library

const RegisterScreen = () => {
  const [username, setUserName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmpassword, setConfirmPassword] = useState("");
  const [phonenumber, setPhoneNumber] = useState("");
  const [nameError, setNameError] = useState({ isError: false, message: "" });
  const [emailError, setEmailError] = useState({ isError: false, message: "" });
  const [passwordError, setPasswordError] = useState({ isError: false, message: "" });
  const [confirmPasswordError, setConfirmPasswordError] = useState({ isError: false, message: "" });
  const [phoneNumberError, setPhoneNumberError] = useState({ isError: false, message: "" });

  const navigation = useNavigation();

  const handleNameChange = (text) => {
    setUserName(text);
    // Clear errors when user starts entering details
    if (nameError.isError) {
      setNameError({ isError: false, message: "" });
    }
  };

  const handleEmailChange = (text) => {
    setEmail(text);
    if (emailError.isError) {
      setEmailError({ isError: false, message: "" });
    }
  };

  const handlePasswordChange = (text) => {
    setPassword(text);
    if (passwordError.isError) {
      setPasswordError({ isError: false, message: "" });
    }
  };

  const handleConfirmPasswordChange = (text) => {
    setConfirmPassword(text);
    if (confirmPasswordError.isError) {
      setConfirmPasswordError({ isError: false, message: "" });
    }
  };

  const handlePhoneNumberChange = (text) => {
    setPhoneNumber(text);
    if (phoneNumberError.isError) {
      setPhoneNumberError({ isError: false, message: "" });
    }
  };

  const handleRegister = () => {
    // Validate fields
    let hasError = false;
    if (!username) {
      setNameError({ isError: true, message: "Please enter your name" });
      hasError = true;
    }
    if (!email) {
      setEmailError({ isError: true, message: "Please enter your email" });
      hasError = true;
    }
    if (!password) {
      setPasswordError({ isError: true, message: "Please enter your password" });
      hasError = true;
    }
    if (!confirmpassword) {
      setConfirmPasswordError({ isError: true, message: "Please confirm your password" });
      hasError = true;
    }
    if (password !== confirmPassword) {
      setPasswordError({ isError: true, message: "Passwords do not match" });
      setConfirmPasswordError({ isError: true, message: "Passwords do not match" });
      hasError = true;
    }
    if (!phonenumber) {
      setPhoneNumberError({ isError: true, message: "Please enter your phone number" });
      hasError = true;
    }

    if (hasError) return;

    // Proceed with registration
    const user = {
      name: username,
      email: email,
      password: password,
      confirmPassword: confirmpassword,
      phoneNumber: phonenumber
    };

    axios.post("http://localhost:3000/users/register", user)
      .then((response) => {
        console.log(response);
        Alert.alert(
          "Registration successful",
          "You have been registered successfully"
        );
        setUserName("");
        setEmail("");
        setPassword("");
        setConfirmPassword("");
        setPhoneNumber("");
      })
      .catch((error) => {
        Alert.alert(
          "Registration failed",
          "An error occurred during registration"
        );
        console.log("error", error);
      });
  };

  return (
    <SafeAreaView style={{ flex: 1, backgroundColor: "white", alignItems: "center" }}>
      <View style={{ marginTop: 50 }}>
        <Image
          style={{ width: 150, height: 100, resizeMode: "contain" }}
          source={logo}
        />
      </View>

      <KeyboardAvoidingView>
        <View style={{ alignItems: "center", justifyContent: "center" }}>
          <Text style={{ fontSize: 17, fontWeight: "bold", marginTop: 25 }}>
            Register to Your Account
          </Text>
        </View>

        <View style={{ marginTop: 30 }}>
          <View
            style={[
              styles.inputContainer,
              nameError.isError && styles.inputContainerError,
            ]}
          >
            <Ionicons
              style={{ marginLeft: 8 }}
              name="person"
              size={24}
              color="gray"
            />
            <TextInput
              value={username}
              onChangeText={handleNameChange}
              placeholderTextColor={"gray"}
              style={styles.input}
              placeholder="Enter your Name"
            />
          </View>
          {nameError.isError && <Text style={styles.errorMessage}>{nameError.message}</Text>}
        </View>

        <View style={{ marginTop: 30 }}>
          <View
            style={[
              styles.inputContainer,
              emailError.isError && styles.inputContainerError,
            ]}
          >
            <MaterialIcons
              style={{ marginLeft: 8 }}
              name="email"
              size={24}
              color="gray"
            />
            <TextInput
              value={email}
              onChangeText={handleEmailChange}
              placeholderTextColor={"gray"}
              style={styles.input}
              placeholder="Enter your Email"
            />
          </View>
          {emailError.isError && <Text style={styles.errorMessage}>{emailError.message}</Text>}
        </View>

        <View style={{ marginTop: 30 }}>
          <View
            style={[
              styles.inputContainer,
              passwordError.isError && styles.inputContainerError,
            ]}
          >
            <AntDesign
              style={{ marginLeft: 8 }}
              name="lock"
              size={24}
              color="gray"
            />
            <TextInput
              secureTextEntry={true}
              value={password}
              onChangeText={handlePasswordChange}
              placeholderTextColor={"gray"}
              style={styles.input}
              placeholder="Enter your Password"
            />
          </View>
          {passwordError.isError && <Text style={styles.errorMessage}>{passwordError.message}</Text>}
        </View>

        <View style={{ marginTop: 30 }}>
          <View
            style={[
              styles.inputContainer,
              confirmPasswordError.isError && styles.inputContainerError,
            ]}
          >
            <AntDesign
              style={{ marginLeft: 8 }}
              name="lock"
              size={24}
              color="gray"
            />
            <TextInput
              secureTextEntry={true}
              value={confirmPassword}
              onChangeText={handleConfirmPasswordChange}
              placeholderTextColor={"gray"}
              style={styles.input}
              placeholder="Confirm Password"
            />
          </View>
          {confirmPasswordError.isError && <Text style={styles.errorMessage}>{confirmPasswordError.message}</Text>}
        </View>

        <View style={{ marginTop: 30 }}>
          <View
            style={[
              styles.inputContainer,
              phoneNumberError.isError && styles.inputContainerError,
            ]}
          >
            <MaterialIcons
              style={{ marginLeft: 8 }}
              name="phone"
              size={24}
              color="gray"
            />
            <TextInput
              value={phonenumber}
              onChangeText={handlePhoneNumberChange}
              placeholderTextColor={"gray"}
              style={styles.input}
              placeholder="Enter your Phone Number"
            />
          </View>
          {phoneNumberError.isError && <Text style={styles.errorMessage}>{phoneNumberError.message}</Text>}
        </View>

        <View style={{ marginTop: 45 }} />

        <Pressable
          onPress={handleRegister}
          style={{
            width: 200,
            backgroundColor: "black",
            padding: 15,
            marginTop: 40,
            marginLeft: "auto",
            marginRight: "auto",
            borderRadius: 6,
          }}
        >
          <Text
            style={{
              textAlign: "center",
              fontWeight: "bold",
              fontSize: 16,
              color: "white",
            }}
          >
            Register
          </Text>
        </Pressable>

        <Pressable
          onPress={() => navigation.goBack()}
          style={{ marginTop: 10 }}
        >
          <Text style={{ textAlign: "center", fontSize: 16 }}>
            Already have an account? Sign In
          </Text>
        </Pressable>
      </KeyboardAvoidingView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  inputContainer: {
    flexDirection: "row",
    alignItems: "center",
    gap: 5,
    borderColor: "#D0D0D0",
    borderWidth: 1,
    paddingVertical: 5,
    borderRadius: 5,
  },
  inputContainerError: {
    borderColor: "red",
  },
  input: {
    color: "gray",
    marginVertical: 10,
    width: 300,
    fontSize: 16,
  },
  errorMessage: {
    color: "red",
    marginTop: 5,
  },
});

export default RegisterScreen;
