import React, { useState, useContext } from "react";
import { View, TextInput, Button, SafeAreaView, Image, Text, StyleSheet } from "react-native";
import { UserType } from "../UserContext";
import axios from "axios";

const CreatePostScreen = () => {
  const { userId } = useContext(UserType);
  const [content, setContent] = useState("");

  const handlePostSubmit = () => {
    console.log("UserID:", userId);
    console.log("Content:", content);

    if (!userId || !content) {
      console.log("Postedby and text fields are required");
      return;
    }
  
    const postData = {
      userId,
      content,
    };
  
    axios.post("http://localhost:3000/posts/create-post", postData)
      .then((response) => {
        console.log("Post created successfully");
        setContent("");
      })
      .catch((error) => {
        console.log("Error creating post", error);
      });
  };
  

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.userInfo}>
        <Image
          style={styles.userAvatar}
          source={{
            uri: "https://cdn-icons-png.flaticon.com/128/149/149071.png",
          }}
        />
        <Text style={styles.username}>Your Username</Text>
      </View>

      <View style={styles.inputContainer}>
        <TextInput
          value={content}
          onChangeText={(text) => setContent(text)}
          placeholder="Type your message..."
          placeholderTextColor="black"
          multiline
          style={styles.input}
        />
      </View>

      <Button onPress={handlePostSubmit} title="Share Post" />
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    padding: 10,
  },
  userInfo: {
    flexDirection: "row",
    alignItems: "center",
    gap: 10,
    padding: 10,
  },
  userAvatar: {
    width: 40,
    height: 40,
    borderRadius: 20,
    resizeMode: "contain",
  },
  username: {
    fontSize: 16,
    fontWeight: "bold",
  },
  inputContainer: {
    flexDirection: "row",
    marginLeft: 10,
  },
  input: {
    flex: 1,
    borderWidth: 1,
    borderColor: "black",
    borderRadius: 5,
    padding: 10,
  },
});

export default CreatePostScreen;
