import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  TextInput,
  FlatList,
  StyleSheet,
  TouchableOpacity,
  Animated,
  KeyboardAvoidingView,
  Platform,
  Image,
  SafeAreaView,
} from "react-native";
import { Feather } from "@expo/vector-icons"; // Import Feather icons from Expo
import axios from "axios";
import { useNavigation } from "@react-navigation/native";
import AsyncStorage from "@react-native-async-storage/async-storage";

const SearchScreen = () => {
  const navigation = useNavigation();
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const handleSearch = async () => {
    try {
      setLoading(true);
      // Make HTTP request to backend API using Axios
      const response = await axios.get(
        `https://levelart.up.railway.app/search/user?username=${searchQuery}`
      );
      setSearchResults(response.data.data); // Update this line
    } catch (error) {
      console.error("Error searching:", error.message);
    } finally {
      setLoading(false);
    }
  };
  console.log("search people", searchResults);
  return (
    <SafeAreaView
      style={{ flex: 1, paddingHorizontal: 10, paddingVertical: 10 }}
    >
      <View style={{ paddingTop: 30, paddingBottom: 100 }}>
        <Text style={{ fontSize: 30, color: "#000", fontWeight: "600" }}>
          Search
        </Text>
        <View
          style={{
            flexDirection: "row",
            height: 45,
            padding: 5,
            backgroundColor: "#0000000e",
            borderRadius: 8,
            justifyContent: "center",
            gap: 10,
            alignItems: "center",
            color: "#000",
            marginTop: 10,
          }}
        >
          <Feather name="search" size={20} color="black" />
          <TextInput
            onChangeText={(e) => handleSearch(e)}
            placeholder="Search"
            placeholderTextColor={"#000"}
            style={{ flex: 1, height: 45 }}
          />
        </View>
        <FlatList
          data={searchResults}
          showsVerticalScrollIndicator={false}
          renderItem={({ item }) => {
            const handleFollowUnfollow = async (user) => {
  try {
    const authToken = await AsyncStorage.getItem("authToken"); // Replace with your actual authentication token

    console.log("authToken:",authToken)
    const headers = {
      Authorization: `Bearer ${authToken}`,
    };

    // Check if the user is already followed
    const isFollowing = user.followers.some(
      (follower) => follower.userId === user._id
    );

    // Make HTTP request based on whether the user is already followed or not
    if (isFollowing) {
      // Unfollow the user
      await axios.post(
        `https://levelart.up.railway.app/followers/${user._id}/unfollow`,
        { followedUserId: user._id },
        { headers }
      );
      // Update the UI to reflect the unfollow action
    } else {
      // Follow the user
      await axios.post(
        `https://levelart.up.railway.app/followers/follow/${user._id}`,
        { followedUserId: user._id },
        { headers }
      );
      // Update the UI to reflect the follow action
    }
  } catch (error) {
    console.error("Error:", error.message);
    // Handle the error (e.g., display a message to the user)
  }
};


            return (
              <TouchableOpacity
                onPress={() =>
                  navigation.navigate("Profile", {
                    item: item,
                  })
                }
              >
                <View
                  style={{
                    flexDirection: "row",
                    marginTop: 13,
                    borderRadius: 10,
                    padding: 3,
                  }}
                >
                  {/* {item.profilePic && ( */}
                    <Image
                      source={{ uri: item.profilePic }}
                      style={{ width: 40, height: 40, borderRadius: 50 }}
                    />
                  {/* )} */}
                  <View
                    style={{
                      flex: 1,
                      flexDirection: "row",
                      justifyContent: "space-between",
                      borderBottomWidth: 1,
                      borderBottomColor: "#00000020",
                      paddingBottom: 2,
                    }}
                  >
                    <View>
                      <View
                        style={{ flexDirection: "row", alignItems: "center" }}
                      >
                        <Text
                          style={{
                            paddingLeft: 3,
                            fontSize: 18,
                            color: "black",
                          }}
                        >
                        {item.fullname.length > 15
                          ? `${item.fullname.slice(0, 15)}...`
                          : item.fullname}
                        </Text>
                        {/* {item?.role === "Admin" && (
                          <Image
                            source={{
                              uri: "https://cdn-icons-png.flaticon.com/128/1828/1828640.png",
                            }}
                            width={18}
                            height={18}
                            style={{ marginLeft: 1 }}
                          />
                        )} */}
                      </View>

                      <Text
                        style={{ paddingLeft: 3, fontSize: 18, color: "black" }}
                      >
                        {item.username.length > 10
                          ? `${item.username.slice(0, 10)}...`
                          : item.username}
                      </Text>
                      <Text
                        style={{
                          paddingLeft: 3,
                          marginTop: 1,
                          fontSize: 16,
                          color: "#444",
                        }}
                      >
                        {item.followers.length} followers
                      </Text>
                    </View>
                    <View>
                    <TouchableOpacity
  style={{
    borderRadius: 8,
    width: 100,
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
    height: 35,
    borderWidth: 1,
    borderColor: "#0000004b",
  }}
  onPress={() => handleFollowUnfollow(item)}
>
  <Text style={{ color: "black" }}>
    {item.followers.find((i) => i.userId === searchResults._id)
      ? "Unfollow"
      : "Follow"}
  </Text>
</TouchableOpacity>

                    </View>
                  </View>
                </View>
              </TouchableOpacity>
            );
          }}
        />
      </View>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "white",
  },
  innerContainer: {
    flex: 1,
    paddingHorizontal: 20,
    paddingTop: 65,
  },
  inputContainer: {
    flexDirection: "row",
    alignItems: "center",
    borderWidth: 2,
    borderColor: "gray",
    borderRadius: 25,
    paddingHorizontal: 15,
    marginBottom: 10,
  },
  input: {
    flex: 1,
    fontSize: 16,
    paddingVertical: 10,
  },
  searchIconContainer: {
    marginLeft: 10,
  },
  loadingText: {
    textAlign: "center",
    marginTop: 20,
    fontSize: 16,
    color: "black",
  },
  resultItem: {
    marginBottom: 10,
    paddingVertical: 15,
    paddingHorizontal: 20,
    borderWidth: 1,
    borderColor: "lightgray",
    borderRadius: 25,
    backgroundColor: "white",
    elevation: 3,
  },
  username: {
    fontWeight: "bold",
    fontSize: 15,
  },
});

export default SearchScreen;
