import React, { useEffect, useState, useContext } from "react";
import { StyleSheet, Text, View, Image, Pressable, FlatList, Dimensions, Button } from "react-native";
import axios from "axios";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useNavigation } from "@react-navigation/native";
import { UserType } from '../UserContext';
import Icon from 'react-native-vector-icons/Ionicons';
import { createMaterialTopTabNavigator } from '@react-navigation/material-top-tabs';

const Tab = createMaterialTopTabNavigator();

const ProfileScreen = () => {
  const [user, setUser] = useState("");
  const [followers, setFollowers] = useState(0);
  const [followings, setFollowings] = useState(0);
  const [posts, setPosts] = useState([]);
  const navigation = useNavigation();
  const { userId } = useContext(UserType);

  useEffect(() => {
    const fetchProfile = async (userId) => {
      try {
        const authToken = await AsyncStorage.getItem("authToken");

        const profileResponse = await axios.get(
          `https://levelart.up.railway.app/followers/${userId}/followers`,
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
            },
          }
        );

        const { user, followers, followings } = profileResponse.data;
        setUser(user);
        setFollowers(followers?.length || 0);
        setFollowings(followings?.length || 0);

        const postsResponse = await axios.get(
          `https://levelart.up.railway.app/posts/user/${userId}`,
          {
            headers: {
              Authorization: `Bearer ${authToken}`,
            },
          }
        );
        setPosts(postsResponse.data);
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    };

    if (userId) {
      fetchProfile(userId);
    } else {
      console.log("userId is undefined");
    }
  }, [userId]);

  const logout = async () => {
    try {
      await AsyncStorage.removeItem("authToken");
      console.log("Cleared auth token");
      navigation.replace("Login");
    } catch (error) {
      console.error("Error clearing auth token:", error);
    }
  };

  const handleSettingsPress = () => {
    navigation.navigate('Settings');
  };

  return (
    <View style={styles.container}>
      <View style={styles.profileStats}>
        <Text style={styles.statsText}>{posts.length} posts</Text>
        <Text style={styles.statsText}>{followers} followers</Text>
        <Text style={styles.statsText}>{followings} following</Text>
      </View>
      <View style={styles.profileHeader}>
        <View>
          <Text style={styles.username}>{user}</Text>
          <Text style={styles.userId}>{userId}</Text>
        </View>
        <Pressable style={styles.settingsIcon} onPress={handleSettingsPress}>
          <Icon name="settings-outline" size={30} color="#333" />
        </Pressable>
      </View>
      <View style={styles.profileInfo}>
        <View style={styles.bioContainer}>
          <Image
            style={styles.profileImage}
            source={{
              uri: "https://cdn-icons-png.flaticon.com/128/149/149071.png",
            }}
          />
          {/* Add bio here */}
        </View>
        <Text style={styles.followText}>{followers} followers</Text>
        <Text style={styles.followText}>{followings} following</Text>
      </View>
      <View style={styles.bioTextContainer}>
        {/* <TextInput
          style={styles.bioInput}
          placeholder=""
          onChangeText={setBio}
          value={bio}
        /> */}
        <Button title="Edit Profile" onPress={updateBio} color="black" />
      </View>
      <Tab.Navigator>
        <Tab.Screen name="Images" component={ImagesScreen} initialParams={{ posts: posts }} />
        <Tab.Screen name="Tweets" component={TweetsScreen} initialParams={{ posts: posts }} />
      </Tab.Navigator>
      <View style={styles.buttonsContainer}>
        <Pressable style={styles.button} onPress={logout}>
          <Text style={styles.buttonText}>Logout</Text>
        </Pressable>
      </View>
    </View>
  );
};

const ImagesScreen = ({ route }) => {
  const { posts } = route.params;

  const renderPostItem = ({ item }) => (
    <View style={styles.postWrapper}>
      <Image
        style={styles.postImage}
        source={{
          uri: item.img || "https://via.placeholder.com/150",
        }}
      />
    </View>
  );

  return (
    <View style={styles.container}>
      <FlatList
        data={posts}
        renderItem={renderPostItem}
        keyExtractor={(item) => item._id}
        contentContainerStyle={styles.postsList}
        numColumns={3} // Adjust the number of columns
      />
    </View>
  );
};

const TweetsScreen = ({ route }) => {
  const { posts } = route.params;

  const renderPostItem = ({ item }) => (
    <View style={styles.postWrapper}>
      <Text style={styles.postText}>{item.text}</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      <FlatList
        data={posts}
        renderItem={renderPostItem}
        keyExtractor={(item) => item._id}
        contentContainerStyle={styles.postsList}
        numColumns={3} // Adjust the number of columns
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  profileStats: {
    flexDirection: "row",
    justifyContent: "space-around",
    alignItems: "center",
    paddingVertical: 10,
    borderBottomWidth: 1,
    borderBottomColor: "#ccc",
  },
  statsText: {
    fontSize: 16,
    fontWeight: "bold",
  },
  profileHeader: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    padding: 15,
  },
  username: {
    fontSize: 20,
    fontWeight: "bold",
  },
  userId: {
    fontSize: 16,
    color: "gray",
  },
  profileImage: {
    width: 100,
    height: 100,
    borderRadius: 50,
    resizeMode: "cover",
  },
  bioContainer: {
    flexDirection: "row",
    alignItems: "center",
    padding: 15,
    borderBottomWidth: 1,
    borderBottomColor: "#ccc",
  },
  buttonsContainer: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    marginTop: 10,
    padding: 15,
  },
  button: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    padding: 10,
    borderWidth: 1,
    borderRadius: 1,
    backgroundColor: "black",
  },
  buttonText: {
    textAlign: "center",
    fontWeight: "bold",
    color: "white",
  },
  settingsIcon: {
    marginLeft: 'auto',
  },
  profileInfo: {
    marginBottom: 20,
  },
  bioTextContainer: {
    alignItems: "center",
    padding: 15,
  },
  followText: {
    color: "gray",
    fontSize: 15,
  },
  postsList: {
    flexGrow: 1,
    padding: 15,
  },
  postWrapper: {
    flex: 1,
    flexDirection: 'grid',
    justifyContent: 'space-between',
    marginBottom: 50,
    alignItems: "left",
  },
  postText: {
    fontSize: 16,
    textAlign: "center",
  },
  postImage: {
    width: Dimensions.get("window").width / 4,
    height: Dimensions.get("window").width / 4,
    resizeMode: "cover",
  },
});

export default ProfileScreen;
