import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View, Image, SafeAreaView, TextInput, Button, ActivityIndicator, TouchableOpacity } from "react-native";
import ImagePicker from 'react-native-image-picker'; // Import the library
import axios from "axios";
import AsyncStorage from "@react-native-async-storage/async-storage";
import jwt_decode from "jwt-decode";

const ThreadsScreen = () => {
  const [postedBy, setPostedBy] = useState("");
  const [content, setContent] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const [image, setImage] = useState(null); // State to store selected image URI

  useEffect(() => {
    const fetchUserId = async () => {
      try {
        const tokenInThreadScreen = await AsyncStorage.getItem("authToken");
        const decodedToken = jwt_decode(tokenInThreadScreen);
        const userId = decodedToken?.sub; // Extract userId from decoded token
        console.log("userIdFromThreadScreen:", userId);
        if (userId) {
          setPostedBy(userId);
        } else {
          console.log("User ID not found in AsyncStorage");
        }
      } catch (error) {
        console.error("Error fetching userId from AsyncStorage:", error);
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchUserId();
  }, []);
  
  const handlePostSubmit = async () => {
    if (!content && !image) {
      console.log("Text field or image is required");
      return;
    }
  
    try {
      const authToken = await AsyncStorage.getItem("authToken");
      const postData = {
        postedBy: postedBy, // Use postedBy state value
        text: content,
        image: image // Include the image URI in the post data
      };
  
      const response = await axios.post("https://levelartbackend-production.up.railway.app/posts/create-post", postData, {
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
      });
  
      setContent("");
      setImage(null);
      console.log("Post created successfully:", response.data);
    } catch (error) {
      console.log("Error creating post:", error);
    }
  };

  const handleImagePicker = () => {
    ImagePicker.showImagePicker({ mediaType: 'photo' }, response => {
      if (response.didCancel) {
        console.log('User cancelled image picker');
      } else if (response.error) {
        console.log('ImagePicker Error: ', response.error);
      } else {
        const source = { uri: response.uri };
        setImage(source.uri);
      }
    });
  };
  
  if (isLoading) {
    return (
      <SafeAreaView style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
        <ActivityIndicator size="large" color="black" />
      </SafeAreaView>
    );
  }

  return (
    <SafeAreaView style={{ padding: 10 }}>
      <View
        style={{
          flexDirection: "row",
          alignItems: "center",
          gap: 10,
          padding: 10,
        }}
      >
        <Image
          style={{
            width: 40,
            height: 40,
            borderRadius: 20,
            resizeMode: "contain",
          }}
          source={{
            uri: "https://cdn-icons-png.flaticon.com/128/149/149071.png",
          }}
        />
      </View>

      <View style={{ flexDirection: "row", marginLeft: 10 }}>
        <TextInput
          value={content}
          onChangeText={(text) => setContent(text)}
          placeholderTextColor={"black"}
          placeholder="Type your message..."
          multiline
        />
      </View>

      <TouchableOpacity onPress={handleImagePicker}>
        <View style={{ marginTop: 10, alignItems: 'center' }}>
          <Text>Add Image</Text>
        </View>
      </TouchableOpacity>

      {image && (
        <Image
          source={{ uri: image }}
          style={{ width: 200, height: 200, marginTop: 10 }}
        />
      )}

      <View style={{ marginTop: 20 }} />

      <Button onPress={handlePostSubmit} title="Share Post" />
    </SafeAreaView>
  );
};

export default ThreadsScreen;

const styles = StyleSheet.create({});
