import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View, Image, SafeAreaView, TextInput, Button, ActivityIndicator } from "react-native";
import axios from "axios";
import AsyncStorage from "@react-native-async-storage/async-storage";

const ThreadsScreen = () => {
  const [postedBy, setPostedBy] = useState("");
  const [content, setContent] = useState("");
  const [isLoading, setIsLoading] = useState(true);


  useEffect(() => {

    console.log("postedBy:",postedBy)
    const fetchUserId = async () => {
      try {

        const storedUserId = await AsyncStorage.getItem("userId")
console.log("storedUserId:",storedUserId)
        // Make a GET request to your backend API to fetch the user ID
        const response = await axios.get(`https://levelartbackend-production.up.railway.app/users/${storedUserId}`);
        const userId = response.data._id; // Assuming your response has a key named "userId"
        console.log("userId:", userId);
        setPostedBy(userId);
        setIsLoading(false); // Set loading state to false after fetching user ID
      } catch (error) {
        console.error("Error fetching userId from MongoDB:", error);
        setIsLoading(false); // Set loading state to false in case of error
      }
    };
  
    fetchUserId();
  }, []);
  
  
  
  const handlePostSubmit = () => {
    if (!postedBy) {
      console.log("Postedby fields are required");
      return;
    }
  
    if (!content) {
      console.log("textField fields are required");
      return;
    }
  
    AsyncStorage.getItem("authToken")
      .then((token) => {
        if (!token) {
          console.log("No token found in AsyncStorage");
          return;
        }
  
        const postData = {
          postedBy: postedBy, // Pass the userId as postedBy
          textField: content, // Pass the content as textField
        };
  
        // Include the token in the request header
        axios.post("https://levelartbackend-production.up.railway.app/posts/create-post", postData, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then((response) => {
          setContent("");
        })
        .catch((error) => {
          console.log("error creating post", error);
        });
      })
      .catch((error) => {
        console.error("Error fetching authToken from AsyncStorage:", error);
      });
  };
  
  if (isLoading) {
    return (
      <SafeAreaView style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
        <ActivityIndicator size="large" color="black" />
      </SafeAreaView>
    );
  }

  return (
    <SafeAreaView style={{ padding: 10 }}>
      <View
        style={{
          flexDirection: "row",
          alignItems: "center",
          gap: 10,
          padding: 10,
        }}
      >
        <Image
          style={{
            width: 40,
            height: 40,
            borderRadius: 20,
            resizeMode: "contain",
          }}
          source={{
            uri: "https://cdn-icons-png.flaticon.com/128/149/149071.png",
          }}
        />
      </View>

      <View style={{ flexDirection: "row", marginLeft: 10 }}>
        <TextInput
          value={content}
          onChangeText={(text) => setContent(text)}
          placeholderTextColor={"black"}
          placeholder="Type your message..."
          multiline
        />
      </View>

      <View style={{ marginTop: 20 }} />

      <Button onPress={handlePostSubmit} title="Share Post" />
    </SafeAreaView>
  );
};

export default ThreadsScreen;

const styles = StyleSheet.create({});
