import React, { useState } from 'react';
import { View, TextInput, StyleSheet, Button, ScrollView, TouchableOpacity, Text } from 'react-native';
import { Ionicons } from '@expo/vector-icons'; // Assuming you are using Expo for vector icons

const ArtistForm = () => {
  const [page, setPage] = useState(1);

  const nextPage = () => {
    setPage(page + 1);
  };

  const previousPage = () => {
    setPage(page - 1);
  };

  const renderInputs = () => {
    switch (page) {
      case 1:
        return (
          <>
            <TextInput placeholder="First Name" style={styles.input} />
            {/* Other inputs */}
          </>
        );
      case 2:
        return (
          <>
            {/* Inputs for page 2 */}
          </>
        );
      // Other cases for additional pages

      case 5:
        return (
          <>
            <TextInput placeholder="Upload Your Photo" style={styles.input} />
            {/* Other inputs */}
            <View style={styles.navigationButtons}>
              <TouchableOpacity onPress={previousPage} style={styles.backButton}>
                <Ionicons name="arrow-back" size={24} color="black" />
                <Text style={styles.backButtonText}>Back</Text>
              </TouchableOpacity>
            </View>
          </>
        );
      default:
        return null;
    }
  };

  return (
    <ScrollView contentContainerStyle={styles.container}>
      {renderInputs()}
      {page < 5 && <Button title="Next Page" onPress={nextPage} />}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    padding: 20,
  },
  input: {
    borderWidth: 1,
    borderColor: 'gray',
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
  },
  navigationButtons: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: 20,
  },
  backButton: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  backButtonText: {
    marginLeft: 5,
  },
});

export default ArtistForm;
