import React, { useState } from "react";
import { View, TextInput, Button, SafeAreaView, Image, Text, Alert } from "react-native";
import axios from "axios";

const ThreadsScreen = () => {
  const [content, setContent] = useState("");

  const handlePostSubmit = () => {
    // Check if content is empty
    if (!content.trim()) {
      Alert.alert("Error", "Content cannot be empty");
      return;
    }

    // Prepare data for the POST request
    const postData = {
      text: content,
      postedBy: us, // Replace with the actual user ID
    };

    // Make the POST request to create a new post
    axios.post("http://localhost:3000/posts/create-post", postData)
      .then((response) => {
        console.log("Post created successfully:", response.data);
        // Optionally, you can show a success message or navigate to another screen
        setContent(""); // Clear the input field after successful post
      })
      .catch((error) => {
        console.error("Error creating post:", error);
        // Display an error message if the request fails
        Alert.alert("Error", "Failed to create post. Please try again later.");
      });
  };

  return (
    <SafeAreaView style={{ padding: 10 }}>
      <View style={{ flexDirection: "row", alignItems: "center", padding: 10 }}>
        <Image
          style={{ width: 40, height: 40, borderRadius: 20, resizeMode: "contain" }}
          source={{ uri: "https://cdn-icons-png.flaticon.com/128/149/149071.png" }}
        />
        <Text>Sujan_Music</Text>
      </View>

      <View style={{ flexDirection: "row", marginLeft: 10 }}>
        <TextInput
          value={content}
          onChangeText={(text) => setContent(text)}
          placeholder="Type your message..."
          multiline
          style={{ borderWidth: 1, borderColor: "gray", borderRadius: 5, padding: 10, marginBottom: 10 }}
        />
      </View>

      <Button onPress={handlePostSubmit} title="Share Post" />
    </SafeAreaView>
  );
};

export default ThreadsScreen;
